              trafficAAVG=trafficAAVG+response.values[i2][1];
              i2A++;
                  }
            }
          if (response.values[i2][1]<(trafficAVG/sampleCount)) 
            {
              if (response.values[i2][2]!=null)
                {
              metricBAVG=metricBAVG+response.values[i2][2];
              trafficBAVG=trafficBAVG+response.values[i2][1];
              i2B++;
                  }
            }
          i2++;
        }
  percentChangeMetric = (((metricAAVG/i2A) - (metricBAVG/i2B))/(((metricAAVG/i2A) + (metricBAVG/i2B))/2)*100)
  percentChangeTraffic = (((trafficAAVG/i2A) - (trafficBAVG/i2B))/(((trafficAAVG/i2A) + (trafficBAVG/i2B))/2)*100)
  var predictedMetric = (metricAVG/sampleCount) + (metricAVG/sampleCount)*((percentChangeMetric/percentChangeTraffic)*$TrafficChangePercent/100)
  console.log("Traffic Below:",trafficBAVG/i2B)
  console.log("Traffic Average:",trafficAVG/sampleCount)
  console.log("Traffic Above:",trafficAAVG/i2A)
  console.log("Traffic Change:",percentChangeTraffic)
  console.log("Metric Min:",metricMIN)
  console.log("Metric Below:",metricBAVG/i2B)
  console.log("Metric Average:",metricAVG/sampleCount)
  console.log("Metric Above:",metricAAVG/i2A)
  console.log("Metric Change:",percentChangeMetric)
  console.log("Predicted Metric:",predictedMetric)
  if (predictedMetric<metricMIN) predictedMetric=metricMIN;
  return metricName+" is "+Number(metricAVG/sampleCount).toFixed(2)+". Predicted "+metricName+" is "+(predictedMetric).toFixed(2)+" modeled with a "+$TrafficChangePercent+"% traffic increase.";
}

